  var urls = [
    "/Images/s01t.png",
    "/Images/s01.png",
    "/Images/s01t.png",
    "/Images/s02t.png",
    "/Images/s01t.png",
    "/Images/s04t.png",
  ];
  var cout = 1;

  useEffect(() => {
    const element = document.getElementsByClassName("s01-image");
    if (element[0]) {
      console.log("ele", element);
      element[0].src = `${urls[cout]}`;
