    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <div className="s01-main">
            <div>
              <img
                className="s01-image"
                onClick={() => openSecondScreen()}
                height="1440px"
                width={"922px"}
              ></img>
            </div>
            <div>content</div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};
export default S01Screen;
