import React, { useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import "./styles/s01.scss";

const S01Screen = () => {
  var urls = ["/Images/s01.png", "/Images/s02t.png", "/Images/s04t.png"];

  var cout = 1;

  useEffect(() => {
    const element = document.getElementsByClassName("s01-image");
    if (element[0]) {
      console.log("ele", element);
      element[0].src = `${urls[cout]}`;
      setInterval(() => {
        element[0].src = `${urls[cout]}`;
        cout == urls.length - 1 ? (cout = 0) : cout++;
      }, 10000);
    }
  }, []);

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col>
          <div className="s01-main">
            <div>
              <img className="s01-image" height="100px" width={"100px"}></img>
            </div>
            <div>content</div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};
export default S01Screen;
