import React, { useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import Image from "react-bootstrap/Image";

const S01Screen = () => {
  var urls = ["/Images/s01.png", "/Images/s02t.png", "/Images/s04t.png"];

  var cout = 1;
  const image = "../images/s01.png";
  console.log(image);

  useEffect(() => {
    const element = document.getElementsByClassName("s01-image");
    console.log("ele", element);
    if (element[0]) {
      element[0].style.backgroundImage = `url("${urls[cout]}")`;
      setInterval(() => {
        element[0].style.backgroundImage = `url("${urls[cout]}")`;
        cout == urls.length - 1 ? (cout = 0) : cout++;
      }, 10000);
    }
  }, []);
  //   $("body").css("background-image", 'url("' + urls[0] + '")');
  //   setInterval(function () {
  //     $("body").css("background-image", 'url("' + urls[cout] + '")');
  //     cout == urls.length - 1 ? (cout = 0) : cout++;
  //   }, 5000);
  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col xs lg="2">
          <img className="s01-image"></img>
        </Col>
      </Row>
    </Container>
  );
};
export default S01Screen;
