import React, { useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";

const S01Screen = () => {
  var urls = [
    "https://pp.userapi.com/c629327/v629327473/db66/r051joYFRX0.jpg",
    "https://www.codeproject.com/KB/GDI-plus/ImageProcessing2/img.jpg",
    "https://img.wikinut.com/img/gycf69_-6rv_5fol/jpeg/0/Best-Friends-Img-Src:Image:-FreeDigitalPhotos.net.jpeg",
    "http://www.travelettes.net/wp-content/uploads/2014/03/IMG_3829-Medium-600x400.jpg",
  ];

  var cout = 1;
  const image = "../images/s01.png";
  console.log(image);

  useEffect(() => {
    const element = document.getElementsByClassName("s01-image");
    console.log("ele", element);
    if (element[0]) {
      element[0].style.backgroundImage = `url("${urls[cout]}")`;
      setInterval(() => {
        element[0].style.backgroundImage = `url("${urls[cout]}")`;
        cout == urls.length - 1 ? (cout = 0) : cout++;
      }, 10000);
    }
  }, []);
  //   $("body").css("background-image", 'url("' + urls[0] + '")');
  //   setInterval(function () {
  //     $("body").css("background-image", 'url("' + urls[cout] + '")');
  //     cout == urls.length - 1 ? (cout = 0) : cout++;
  //   }, 5000);
  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col xs lg="2">
          1 of 3
        </Col>
      </Row>
    </Container>
  );
};
export default S01Screen;
